package Java;

public class ArithmeticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int b=20;
		
		int d=30;
		
		int c=b+d;
		System.out.println(c);
		
		int e=b*d;
		System.out.println(e);
		int f=d/b;
		System.out.println(f);
		int g=d%b;
		System.out.println(g);
	}

}
