package Java;

public class ImplicitConversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c='a';
		int k=c;
		float f=c;
		long l=c;
		double d=c;
		System.out.println(c);
		System.out.println(k);
		System.out.println(f);
		System.out.println(l);
		System.out.println(d);
		
		
	}

}
