package Java;

public class LogicalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=40;
		int ter=20;
		int d=10;
		int b=20;
		
		boolean c=a<b&&d<b;
		System.out.println(c);
		
		boolean e=a>b && ter<a;
		System.out.println(e);
		
	}

}
