package Java;

public class ShiftAndRelationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=a<<2;
		System.out.println(b);
		int c=a>>3;
		System.out.println(c);
		
		// Relational Operator
		boolean d=a<b;
		System.out.println(d);
		
		boolean e=a>b;
		System.out.println(e);
		
		
		
		
	}

}
