package Java;

public class UniryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		x++; // x=x+10 post increment
		System.out.println(x);
		
		int a=10;
		--a; //a=a-1; pre decrement
		System.out.println(a);
		
		boolean a1=true;
		boolean d=!a1;
		System.out.println(d);
		
	}

}
